{
  "title": "Customer Account Details",
  "description": "Customer Account Details",
  "meta:intendedToExtend": [
    "https://ns.adobe.com/xdm/context/profile"
  ],
  "type": "object",
  "definitions": {
    "customFields": {
      "type": "object",
      "properties": {
        "_devbc": {
          "type": "object",
          "properties": {
            "customerID": {
              "title": "Customer ID",
              "description": "Customer ID.",
              "type": "string"
            },
            "account": {
              "title": "Account Setting Details",
              "description": "Account Details",
              "type": "object",
              "properties": {
                "createDate": {
                  "title": "Account Create Date",
                  "description": "Account create date.",
                  "type": "string",
                  "format": "date-time"
                },
                "endDate": {
                  "title": "Account End Date",
                  "description": "Account end date.",
                  "type": "string",
                  "format": "date-time"
                },
                "acqSource": {
                  "title": "Account Acquisition Source",
                  "description": "Account acquisition source.",
                  "type": "string",
                  "enum": [
                    "web",
                    "inStore"
                  ],
                  "meta:enum": {
                    "web": "Web",
                    "inStore": "In Store"
                  }
                }
              }
            },
            "plan": {
              "title": "Plan  ",
              "description": "Plan Details",
              "type": "object",
              "properties": {
                "planID": {
                  "title": "Plan ID",
                  "description": "Plan identifier.",
                  "type": "string"
                },
                "name": {
                  "title": "Plan Name",
                  "description": "Plan name.",
                  "type": "string"
                }
              }
            }
          }
        }
      }
    }
  },
  "allOf": [
    {
      "$ref": "#/definitions/customFields"
    }
  ]
}

